// Obtenemos todos los workflows desde el nodo "Captura de Workflows"
const workflows = $items("Captura de Workflows");

// Crear un mapa agrupado por carpetas
const carpetas = {};

for (const item of workflows) {
  const data = item.json;
  const nombreWorkflow = data.name || "Sin nombre";
  const carpeta = data.meta?.folderName || "Sin carpeta";

  if (!carpetas[carpeta]) {
    carpetas[carpeta] = [];
  }

  carpetas[carpeta].push(nombreWorkflow);
}

// Generar texto formateado
let texto = "✔️ Respaldo diario finalizado\n\n";

// Agregar carpetas y workflows dentro de cada una
for (const [carpeta, lista] of Object.entries(carpetas)) {
  texto += `📁 ${carpeta}\n`;
  for (const wf of lista) {
    texto += `  📄 ${wf}\n`;
  }
}

// Totales
const totalWorkflows = workflows.length;
const totalCarpetas = Object.keys(carpetas).length;

// Obtener datos adicionales desde "Establecer campos"
const fecha = $node["Establecer campos"]?.item?.json?.commitDate || new Date().toISOString();

// Agregar pie del mensaje
texto += `\n📊 Total: ${totalWorkflows} workflows\n`;
texto += `📁 Carpetas: ${totalCarpetas}\n`;
texto += `🗓 Fecha: ${fecha}\n`;

return [{ json: { mensaje: texto } }];

